    $where[\'Link.master\'] = $master;
} elseif (!empty($link) && !empty($slave)) {
    $innerJoin[\'Link\'] = array(
        \'class\' => \'msProductLink\',
        \'on\' => \'msProduct.id = Link.master AND Link.link = \' . $link,
