$output = array();
if (!empty($rows) && is_array($rows)) {
    $c = $modx->newQuery(\'modPluginEvent\', array(\'event:IN\' => array(\'msOnGetProductPrice\', \'msOnGetProductWeight\', \'msOnGetProductFields\')));
